import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css'],
})
export class NavbarComponent {
  scrollToTargetAdjusted(target: string): void {
    let element = document.getElementById(target);
    let headerOffset = 45;
    if (element) {
      let elementPosition = element.getBoundingClientRect().top;
      let offsetPosition = elementPosition + window.pageYOffset - headerOffset;

      window.scrollTo({
        top: offsetPosition,
        behavior: 'smooth',
      });
    }
  }

  menuCollapsed = true;

  constructor(private router: Router) {}

  isActive(path: string): boolean {
    return this.router.url === path;
  }

  toggleMenu(): void {
    this.menuCollapsed = !this.menuCollapsed;
  }
}
<nav
  class="h-16 secondary text-lg rounded-3xl md:container md:mx-auto m-4 md:mt-8 shadow-md p-4 flex flex-row justify-evenly md:justify-between items-center text-sm md:text-lg">
  <div>
    <ul class="flex flex-row md:gap-4 justify-between">
      <li class="">
        <a href="/" class="nav-link rounded-3xl p-3"
          [ngClass]="{'secondary-button secondary-hover-override': isActive('/')}">Home</a>
      </li>
      <li class="">
        <!-- call scrollToTargetAdjusted -->
        <button class="nav-link rounded-3xl p-3 m-0" [ngClass]="{'secondary-button': isActive('/about')}"
          (click)="scrollToTargetAdjusted('about-me')">About</button>
      </li>
      <li class="">
        <a href="/portfolio" class="nav-link rounded-3xl p-3"
          [ngClass]="{'secondary-button': isActive('/portfolio')}">Portfolio</a>
      </li>
      <li>
        <a href="/contact" class="nav-link rounded-3xl p-3"
          [ngClass]="{'secondary-button': isActive('/contact')}">Contact</a>
      </li>
    </ul>
  </div>
  <div class="hidden md:block">
    <a href="/" class="font-bold">Reed Sloan</a>
  </div>
</nav>
<div class="py-4 md:py-8"></div>
